/* jshint es3: false */
/* globals wc_epayco_daviplata_checkout_params, CheckoutDaviplataElements, CheckoutDaviplataPage */
(function ($) {
    'use strict';

    $(function () {
        var epayco_submit_daviplata = false;

        // Handler form submit
        function epaycoFormHandlerDaviplata() {
            if (!document.getElementById('payment_method_woo-epayco-daviplata').checked) {
                return true;
            }
            let daviplataContent = document.querySelector("form.checkout").getElementsByClassName("ep-checkout-daviplata-content")[0];
            //let daviplataContent = document.querySelector(CheckoutDaviplataElements.daviplataContent);
            let daviplataHelpers = daviplataContent.querySelectorAll('input-helper');

            const ticketName = document.getElementsByName('epayco_daviplata[name]')[0]??document.getElementsByName('epayco_daviplata[nameError]')[0];
            const daviplataContentName = ticketName.value;
            const nameHelpers =  daviplataContent.querySelector('input-helper-epayco').querySelector("div");
            verifyName(daviplataContent,daviplataContentName, nameHelpers)

            const daviplataContentEmail = document.getElementsByName('epayco_daviplata[email]')[0].value;
            const emailHelpers =  daviplataContent.querySelector('input-email-epayco').querySelector("input-helper-epayco").querySelector("div");
            verifyEmail(daviplataContent,daviplataContentEmail,emailHelpers)

            const cellphoneType = document.getElementsByName('epayco_daviplata[cellphone]')[0].value;
            const daviplataContentCellphone = document.getElementsByName('epayco_daviplata[cellphoneType]')[0].value;
            const cellphoneHelpers =  daviplataContent.querySelector('input-cellphone-epayco').querySelector("input-helper-epayco").querySelector("div");
            verifyCellphone(daviplataContent,cellphoneType,cellphoneHelpers)

            const doc_type = document.getElementsByName('epayco_daviplata[documentType]')[0].value;
            const documentHelpers =  daviplataContent.querySelector('input-document-epayco').querySelector("input-helper-epayco").querySelector("div");
            const doc_number = document.getElementsByName('epayco_daviplata[document]').length>0?document.getElementsByName('epayco_ticket[document]'):document.getElementsByName('documentTypeError');
            const doc_number_value = doc_number[0].value;
            verifyDocument(daviplataContent,doc_number_value, documentHelpers);

            verifyTermAndCondictions(daviplataContent)
            let checked =  daviplataContent.parentElement.querySelector('terms-and-conditions').querySelector('input').checked
            
            if (checkForErrors(daviplataHelpers) || !checked) {
                removeBlockOverlay();
            } else {
                epayco_submit_daviplata = true;
            }

            return epayco_submit_daviplata;
        }

        function checkForErrors(daviplataHelpers) {
            let hasError = false;

            daviplataHelpers.forEach((item) => {
                let inputHelper = item.querySelector('div');
                if (inputHelper.style.display !== 'none') {
                    hasError = true;
                }
            });

            return hasError;
        }

        function verifyName(daviplataContent,daviplataContentName, nameHelpers) {
            if (daviplataContentName === '' || daviplataContentName.length < 2) {
                daviplataContent.querySelector('input-name-epayco').querySelector(".ep-input").classList.add("ep-error");
                nameHelpers.style.display = 'flex';
            }
        }

        function verifyEmail(daviplataContent,daviplataContentEmail,emailHelpers) {
            if (daviplataContentEmail === '') {
                daviplataContent.querySelector('input-email-epayco').querySelector(".ep-input").classList.add("ep-error");
                emailHelpers.style.display = 'flex';
            }
        }

        function verifyCellphone(daviplataContent,cellphoneType,cellphoneHelpers) {
            if (cellphoneType === '') {
                daviplataContent.querySelector('input-cellphone-epayco').querySelector(".ep-input").classList.add("ep-error");
                daviplataContent.querySelector('input-cellphone-epayco').querySelector(".ep-input").parentElement.lastChild.classList.add("ep-error");
                cellphoneHelpers.style.display = 'flex';
            }
        }

        function verifyDocument(daviplataContent,daviplataContentDocument, documentHelpers) {
            if (daviplataContentDocument === '') {
                daviplataContent.querySelector('input-document-epayco').querySelector(".ep-input").classList.add("ep-error");
                daviplataContent.querySelector('input-document-epayco').querySelector(".ep-input").parentElement.lastChild.classList.add("ep-error");
                documentHelpers.style.display = 'flex';
            }
        }


        function removeErrorFromInputTableContainer(daviplataContent) {
            daviplataContent.querySelectorAll('.ep-input-table-label').forEach((item) => {
                item.addEventListener('click', () => {
                    let documentElement = daviplataContent.querySelector('.ep-checkout-daviplata-payment-method');
                    documentElement.querySelector("input-table > div").classList.remove("ep-error")
                    let pseHelpers = documentElement.querySelector('input-helper');
                    let child = pseHelpers.querySelector('div');
                    child.style.display = 'none';
                });
            });
        }

        function verifyTermAndCondictions(daviplataContent) {
            let addressElement = daviplataContent.parentElement.querySelector('terms-and-conditions').querySelector('input');
            if (!addressElement.checked) {
                daviplataContent.parentElement.querySelector('terms-and-conditions > div').classList.add('ep-error')
            }
        }

        // Process when submit the checkout form
        $('form.checkout').on('checkout_place_order_woo-epayco-daviplata', function () {
            return epaycoFormHandlerDaviplata();
        });

        // If payment fail, retry on next checkout page
        $('form#order_review').submit(function () {
            return epaycoFormHandlerDaviplata();
        });

        // Remove Block Overlay from Order Review page
        function removeBlockOverlay() {
            if ($('form#order_review').length > 0) {
                $('.blockOverlay').css('display', 'none');
            }
        }
    });
})(jQuery);
